import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Table from "react-bootstrap/Button";

import "./App.css";

class PictureOfDay extends Component {
  render() {
    if (!this.props.img) {
      return null;
    }
    return (
      <div className="header">
        <div className="header-img">
          <img src={this.props.img} />
        </div>
      </div>
    );
  }
}

class MovieDetail extends Component {
  render() {
    if (!this.props.movie) {
      return null;
    }

    return (
      <div key={this.props.movie.title} className="text-center">
        <div className="card shadow">
          <img
            src={`http://image.tmdb.org/t/p/w185/${this.props.movie.poster_path}`}
            className="card-img-top"
          />
          <div className="card-body">
            <h5 className="card-title">{this.props.movie.title}</h5>
            <p className="card-text">{this.props.movie.overview}</p>
          </div>
        </div>
      </div>
    );
  }
}

class Asteroid extends Component {
  render() {
    return (
      <tr key={this.props.asteriod.name}>
        <td>{this.props.asteriod.date}</td>
        <td>{this.props.asteriod.name}</td>
        <td>{this.props.asteriod.estimated_diameter_max}</td>
        <td>{this.props.asteriod.miss_distance}</td>
      </tr>
    );
  }
}

class Poster extends Component {
  render() {
    return (
      <div key={this.props.movie.title} className="col-3 text-center">
        <img
          onMouseEnter={e => {
            this.props.whenClicked(this.props.movie);
          }}
          src={`http://image.tmdb.org/t/p/w185/${this.props.movie.poster_path}`}
          className="img-fluid"
          alt={`Poster for ${this.props.movie.title}`}
        ></img>
        <p>{this.props.movie.release_date.substr(0, 4)}</p>
      </div>
    );
  }
}

class App extends Component {
  constructor(props) {
    super(props);
