import React, { Component } from "react";

class CurrentStocks extends Component {
  constructor(props) {
    super(props);
    this.state = {
      numOfShares: null,
      sell: true,
      selected: false
    };
  }

  operationType(e) {
    const choice = e.currentTarget.innerHTML;
    console.log(e.currentTarget.parentNode.previousSibling);
    e.currentTarget.parentNode.previousSibling.innerHTML = choice;
    if (choice === "Buy") {
      console.log("isbuy");
      this.setState({ sell: false });

      this.setState({ selected: true });
    } else if (choice === "Sell") {
      console.log("isell");

      this.setState({ sell: true });

      this.setState({ selected: true });
    }
    console.log("here buy state");
  }

  amountChange(e) {
    const num = e.currentTarget.value;
    this.setState({ numOfShares: num });
  }

  confirm(e) {
    if (this.state.selected === false) {
      alert("Choose sell/bull first");
      e.currentTarget.previousSibling.value = "";
      return;
    }
    console.log("send");
    console.log(this.state.sell);
    var numOfShares = parseInt(this.state.numOfShares);
    if (this.state.sell === true) {
      if (numOfShares > this.props.stock.numOfShares) {
        console.log(e.currentTarget.previousSibling);
        alert("You cannot sell more than what you own");
        e.currentTarget.previousSibling.value = "";
        return;
      }
      console.log("sell");
      numOfShares = -Math.abs(numOfShares);
    }
    this.setState({ numOfShares: numOfShares });
    console.log("here sharenum befor send data ");
    console.log(numOfShares);
    console.log(e.currentTarget.previousSibling);
    e.currentTarget.previousSibling.value = "";
    this.props.change(this.props.stock.symbol, numOfShares);
    e.currentTarget.previousSibling.previousSibling.firstElementChild.innerHTML =
      "Action";
  }

  render() {
    return (
      <tr>
        <td id="symbol" style={{ width: "8%" }}>
          {this.props.stock.symbol}
        </td>
        <td style={{ width: "30%" }}>{this.props.stock.company}</td>
        <td style={{ width: "14%" }}>{this.props.stock.numOfShares}</td>
        <td style={{ width: "10%" }}>${this.props.stock.price}</td>
        <td style={{ width: "13%" }}>
          ${this.props.stock.totalValue.toFixed(2)}
        </td>
        <td style={{ width: "26%", padding: "3px", paddingRight: "8px" }}>
          <div className="input-group mb-3" style={{ marginBottom: "0px" }}>
            <div className="input-group-prepend">
              <button
                className="btn-outline-secondary dropdown-toggle"
                type="button"
                data-toggle="dropdown"
                data-boundary="window"
                aria-haspopup="true"
                aria-expanded="false"
              >
                Action
              </button>
              <div className="dropdown-menu">
                <li
                  className="dropdown-item"
                  value="buy"
                  onClick={e => this.operationType(e)}
                >
                  Buy
                </li>
                <li
                  className="dropdown-item"
                  value="sell"
                  onClick={e => this.operationType(e)}
                >
                  Sell
                </li>
              </div>
            </div>
            <input
              type="number"
              className="form-control"
              aria-label="Text input with dropdown button"
              min="1"
              onChange={e => this.amountChange(e)}
            />
            <button
              className="btn-outline-secondary"
              type="button"
              onClick={e => this.confirm(e)}
            >
              Confirm
            </button>
          </div>
        </td>
      </tr>
    );
  }
}

export default CurrentStocks;
