import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Table from "react-bootstrap/Table";

import "./App.css";

import StarfieldAnimation from "react-starfield-animation";

class Star extends Component {
  render() {
    return (
      <StarfieldAnimation
        style={{
          position: "absolute",
          width: "100%",
          height: "600px"
        }}
      />
    );
  }
}

class Asteroid extends Component {
  render() {
    var str = "";
    var is_precent = 0.3;
    if (this.props.asteriod.is_hazardous) {
      str = "Yes";
      is_precent = 0.8;
    } else {
      str = "No";
    }

    var dia_precent =
      (this.props.asteriod.estimated_diameter_max -
        this.props.asteriod.min_diameter +
        0.01) /
      (this.props.asteriod.max_diameter - this.props.asteriod.min_diameter);

    var dis_percent =
      1 -
      (this.props.asteriod.miss_distance - this.props.asteriod.min_dis) /
        (this.props.asteriod.max_dis - this.props.asteriod.min_dis);

    var tdStyle = {
      background: "RGB(0,255,255," + dia_precent + ")"
    };

    var isStyle = {
      background: "RGB(255,0,0," + is_precent + ")"
    };

    var disStyle = {
      background: "RGB(255,0,255," + dis_percent + ")"
    };

    return (
      <tr key={this.props.asteriod.name}>
        <td>{this.props.asteriod.date}</td>
        <td>{this.props.asteriod.name}</td>
        <td style={tdStyle}>{this.props.asteriod.estimated_diameter_max}</td>
        <td style={disStyle}>{this.props.asteriod.miss_distance}</td>
        <td style={isStyle}>{str}</td>
      </tr>
    );
  }
}

var diameter_max = 0;

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      purchased: [],
      notPurchased: []
    };
  }

  componentDidMount() {
    if (localStorage.getItem("purchased") === null) {
      console.log("download");
      const symbols = [
        "CSCO",
        "ADBE",
        "NEW",
        "BAC",
        "A",
        "F",
        "C",
        "D",
        "X",
        "O",
        "K",
        "I",
        "V",
        "ATVI",
        "TXN",
        "AA",
        "DIS",
        "AMZN",
        "GOOGL",
        "XOM",
        "JNJ",
        "GE",
        "REAL",
        "MMM"
      ];
      var all = [];
      for (var j = 0; j < symbols.length; j++) {
        const url = this.urlForStockInfo(symbols[j]);
        fetch(url)
          .then(r => r.json())
          .then(data => {
            console.log("here is data");
            console.log(data);
            var stocks = {};
            stocks["symbol"] = data.symbol;
            stocks["company"] = data.companyName;
            stocks["shareNum"] = 0;
            stocks["price"] = data.latestPrice;
            stocks["totalValue"] = 0;
            all.push(stocks);

            this.setState({ notPurchased: all });
            //console.log(stock);
            //console.log(JSON.stringify(allstocks));
          });
      }

      localStorage.setItem("notPurchased", JSON.stringify(all));
    } else {
      this.setState({
        purchased: JSON.parse(localStorage.getItem("purchased"))
      });
      this.setState({
        notPurchased: JSON.parse(localStorage.getItem("notPurchased"))
      });
      this.setState({
        totalShareValue: parseFloat(localStorage.getItem("totalShareValue"))
      });
    }
  }

  render() {
    return <div className="container"></div>;
  }

  urlForStockInfo = stockSymbol => {
    const api_key_param = "token=pk_0450ab0ad32942a480c3e7330fd353c5 ";
    const base_url = "https://cloud.iexapis.com/stable/stock/";
    const api_url = `${base_url}${stockSymbol}/quote?${api_key_param}`;
    return api_url;
  };
}

export default App;
