import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Table from "react-bootstrap/Table";
import ParticlesBg from "particles-bg";
import CurrentStocks from "./CurrentStocks";
import StockMarket from "./StockMarket";

import "./App.css";

import StarfieldAnimation from "react-starfield-animation";

class Star extends Component {
  render() {
    return (
      <StarfieldAnimation
        style={{
          position: "absolute",
          width: "100%",
          height: "600px"
        }}
      />
    );
  }
}

class Asteroid extends Component {
  render() {
    var str = "";
    var is_precent = 0.3;
    if (this.props.asteriod.is_hazardous) {
      str = "Yes";
      is_precent = 0.8;
    } else {
      str = "No";
    }

    var dia_precent =
      (this.props.asteriod.estimated_diameter_max -
        this.props.asteriod.min_diameter +
        0.01) /
      (this.props.asteriod.max_diameter - this.props.asteriod.min_diameter);

    var dis_percent =
      1 -
      (this.props.asteriod.miss_distance - this.props.asteriod.min_dis) /
        (this.props.asteriod.max_dis - this.props.asteriod.min_dis);

    var tdStyle = {
      background: "RGB(0,255,255," + dia_precent + ")"
    };

    var isStyle = {
      background: "RGB(255,0,0," + is_precent + ")"
    };

    var disStyle = {
      background: "RGB(255,0,255," + dis_percent + ")"
    };

    return (
      <tr key={this.props.asteriod.name}>
        <td>{this.props.asteriod.date}</td>
        <td>{this.props.asteriod.name}</td>
        <td style={tdStyle}>{this.props.asteriod.estimated_diameter_max}</td>
        <td style={disStyle}>{this.props.asteriod.miss_distance}</td>
        <td style={isStyle}>{str}</td>
      </tr>
    );
  }
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      hold: [],
      market: []
    };
  }

  componentDidMount() {
    console.log(localStorage.getItem("market"));
    console.log(localStorage.getItem("market").length);
    if (localStorage.getItem("market").length < 5) {
      console.log("market is null");
      const symbols = [
        "CSCO",
        "ADBE",
        "NEW",
        "BAC",
        "A",
        "F",
        "C",
        "D",
        "X",
        "O",
        "K",
        "I",
        "V",
        "ATVI",
        "TXN",
        "AA",
        "DIS",
        "AMZN",
        "GOOGL",
        "XOM",
        "JNJ",
        "GE",
        "REAL",
        "MMM"
      ];
      var all = [];
      for (var i = 0; i < symbols.length; i++) {
        const url = this.urlForStockInfo(symbols[i]);
        fetch(url)
          .then(r => r.json())
          .then(data => {
            console.log("here is data");
            console.log(data);
            var stocks = {};
            stocks["symbol"] = data.symbol;
            stocks["company"] = data.companyName;
            stocks["numOfShares"] = 0;
            stocks["price"] = data.latestPrice;
            stocks["totalValue"] = 0;
            all.push(stocks);
            this.setState({ market: all });
          });
      }
      localStorage.setItem("market", JSON.stringify(all));
      this.setState({
        hold: JSON.parse(localStorage.getItem("hold"))
      });
    } else {
      this.setState({
        hold: JSON.parse(localStorage.getItem("hold"))
      });
      this.setState({
        market: JSON.parse(localStorage.getItem("market"))
      });
      this.setState({
        totalShareValue: parseFloat(localStorage.getItem("totalShareValue"))
      });
    }
  }

  sort(e) {
    const target = e.currentTarget.getAttribute("value");
    var sortState = this.state.sortState;
    if (
      this.state.sortState[target] === 0 ||
      this.state.sortState[target] === -1
    ) {
      this.state.purchased.sort(function(a, b) {
        return a[target] < b[target] ? -1 : 1;
      });
      sortState[target] = 1;
    } else if (this.state.sortState[target] === 1) {
      this.state.purchased.sort(function(a, b) {
        return a[target] < b[target] ? 1 : -1;
      });
      sortState[target] = -1;
    }
    this.setState({ sortState: sortState });
    this.setState({ purchased: this.state.purchased });
  }

  purchaseStock = (symbol, numOfShares) => {
    var market = this.state.market,
      hold = this.state.hold;
    var newStock = {};
    for (var i = 0; i < this.state.market.length; i++) {
      if (this.state.market[i]["symbol"] === symbol) {
        newStock["symbol"] = symbol;
        newStock["price"] = this.state.market[i]["price"];
        newStock["numOfShares"] = numOfShares;
        newStock["company"] = this.state.market[i]["company"];
        newStock["totalValue"] =
          parseFloat(numOfShares) * parseFloat(this.state.market[i]["price"]);
      }
    }
    hold.push(newStock);
    localStorage.setItem("market ", JSON.stringify(market));
    this.setState({ hold: hold });
    localStorage.setItem("hold", JSON.stringify(hold));
    var totalShareValue =
      parseFloat(this.state.totalShareValue) +
      parseFloat(newStock["totalValue"]);
    totalShareValue = totalShareValue.toFixed(2);
    this.setState({ totalShareValue: totalShareValue });
    localStorage.setItem("totalShareValue", totalShareValue.toString());
  };

  render() {
    let config = {
      num: [4, 7],
      rps: 0.1,
      radius: [5, 40],
      life: [1.5, 3],
      v: [2, 3],
      tha: [-40, 40],
      alpha: [0.6, 0],
      scale: [0.1, 0.4],
      position: "all",
      color: ["random", "#ff0000"],
      cross: "dead",
      // emitter: "follow",
      random: 15
    };

    if (Math.random() > 0.85) {
      config = Object.assign(config, {
        onParticleUpdate: (ctx, particle) => {
          ctx.beginPath();
          ctx.rect(
            particle.p.x,
            particle.p.y,
            particle.radius * 2,
            particle.radius * 2
          );
          ctx.fillStyle = particle.color;
          ctx.fill();
          ctx.closePath();
        }
      });
    }

    const recommends = this.state.market.map(stock => {
      return <StockMarket stock={stock} purchase={this.purchaseStock} />;
    });

    return (
      <div className="container">
        <ParticlesBg type="custom" config={config} bg={true} />

        <div className="content">
          <div className="purchase">
            <div
              id="title"
              style={{ color: "white", textAlign: "left", width: "500px" }}
            >
              Recommended Stock
            </div>
            <table id="recommend" className="table table-fixed">
              <thead>
                <tr>
                  <th id="symbol" style={{ width: "15%" }}>
                    Symbol
                  </th>
                  <th style={{ width: "40%" }}>Company Name</th>
                  <th style={{ width: "20%" }}>Market Price</th>
                  <th style={{ width: "12%" }}>Amount</th>
                  <th id="submit" style={{ width: "13%" }}>
                    Operation
                  </th>
                </tr>
              </thead>
              <tbody>{recommends}</tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }

  urlForStockInfo = stockSymbol => {
    const api_key_param = "token=pk_0450ab0ad32942a480c3e7330fd353c5 ";
    const base_url = "https://cloud.iexapis.com/stable/stock/";
    const api_url = `${base_url}${stockSymbol}/quote?${api_key_param}`;
    return api_url;
  };
}

export default App;
