import React, { Component } from "react";
import "./App.css";

class MovieDetail extends Component {
  render() {
    if (!this.props.movie) {
      return null;
    }

    return (
      <div key={this.props.movie.title} className="text-center">
        <div className="card shadow">
          <img
            src={`http://image.tmdb.org/t/p/w185/${this.props.movie.poster_path}`}
            className="card-img-top"
          />
          <div className="card-body">
            <h5 className="card-title">{this.props.movie.title}</h5>
            <p className="card-text">{this.props.movie.overview}</p>
          </div>
        </div>
      </div>
    );
  }
}
class Poster extends Component {
  render() {
    return (
      <div key={this.props.movie.title} className="col-3 text-center">
        <img
          onMouseEnter={e => {
            this.props.whenClicked(this.props.movie);
          }}
          src={`http://image.tmdb.org/t/p/w185/${this.props.movie.poster_path}`}
          className="img-fluid"
          alt={`Poster for ${this.props.movie.title}`}
        ></img>
        <p>{this.props.movie.release_date.substr(0, 4)}</p>
      </div>
    );
  }
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listOfMovies: [],
      selectedMovie: null,
      listOfAsteroids: [],
      selectedAsteroids: null
    };
  }

  componentDidMount() {
    const url = this.urlForTMDB("now_playing");

    var today = new Date();
    var dd = String(today.getDate()).padStart(2, "0");
    var mm = String(today.getMonth() + 1).padStart(2, "0");
    var yyyy = today.getFullYear();
    today = yyyy + "-" + mm + "-" + dd;

    var end_day = new Date();
    end_day.setDate(end_day.getDate() + 7);
    var edd = String(end_day.getDate()).padStart(2, "0");
    var emm = String(end_day.getMonth() + 1).padStart(2, "0");
    var eyyyy = end_day.getFullYear();
    end_day = eyyyy + "-" + emm + "-" + edd;

    const url_aster_all = this.urlForAsterInfo(today, end_day);

    fetch(url)
      .then(r => r.json())
      .then(data => {
        console.log(data);
        this.setState({ listOfMovies: data.results });
      });

    fetch(url_aster_all)
      .then(r => r.json())
      .then(data => {
        console.log(data);
        console.log(data.near_earth_objects["2020-02-23"]);
        var asters = {
          info: []
        };
        for (var i in data.near_earth_objects["2020-02-23"]) {
          var item = data.near_earth_objects["2020-02-23"][i];
          console.log(item);
          asters.info.push({
            date: "12",
            name: item.name,
            estimated_diameter_max:
              item.estimated_diameter.meters.estimated_diameter_max,
            is_hazardous: item.is_potentially_hazardous_asteroid,
            miss_distance: item.close_approach_data.miss_distance
          });
        }

        console.log(asters);
        //console.log(asters.info[item]);
        //console.log(this.flatten(data["near_earth_objects"]));
        console.log(data["near_earth_objects"]);
        console.log(data["near_earth_objects"][0]);

        this.setState({ listOfAsteroids: data.results });
      });
  }
  // let names = ["Jeff", "Ariana", "Bob"]
  // let length_of_names = names.map(function(item) {
  //   return item.length;
  // })  // [4, 6, 3]

  handlePosterClick = movie => {
    this.setState({ selectedMovie: movie });
  };
  render() {
    const posters = this.state.listOfMovies.map(movie => {
      return <Poster movie={movie} whenClicked={this.handlePosterClick} />;
    });
    return (
      <div className="container mt-5">
        <div className="row mb-5">
          <div className="col">
            <button
              onClick={this.handleTopRatedClick}
              className="btn btn-primary"
              href="#"
            >
              Get Top-Rated Movies
            </button>
            <button
              onClick={this.handleNowPlayingClick}
              className="ml-3 btn btn-primary"
              href="#"
            >
              Now Playing
            </button>
          </div>
        </div>
        <div className="row">
          <div className="col-sm-3">
            <MovieDetail movie={this.state.selectedMovie} />
          </div>
          <div className="col-sm-9">
            <div id="wall" className="row">
              {posters}
            </div>
          </div>
        </div>
      </div>
    );
  }

  handleNowPlayingClick = e => {
    e.preventDefault();
    const url = this.urlForTMDB("now_playing");

    fetch(url)
      .then(r => r.json())
      .then(data => {
        console.log(data);
        this.setState({ listOfMovies: data.results });
        // this.state.listOfMovies = data.results
      });
  };
  handleTopRatedClick = e => {
    e.preventDefault();
    const url = this.urlForTMDB("top_rated");

    fetch(url)
      .then(r => r.json())
      .then(data => {
        console.log(data);
        this.setState({ listOfMovies: data.results });
        // this.state.listOfMovies = data.results
      });
  };

  urlForTMDB = resource => {
    const api_key_param = "api_key=bde024f3eb43f597aafe01ed9c9098c6";
    const language_param = "language=en-US";
    const region_param = "region=US";
    const filter_param = "include_adult=false";
    const base_url = `https://api.themoviedb.org/3/movie/${resource}`;
    const api_url = `${base_url}?${api_key_param}&${language_param}&${region_param}&${filter_param}`;
    return api_url;
  };

  urlForAsterInfo = (start_date, end_date) => {
    const base_url = "https://api.nasa.gov/neo/rest/v1/feed?";
    const api_key = "api_key=BWwii9sVzbeaMDa4dfvjdvgT5Q30VgEgdyRmibIf";
    const api_url = `${base_url}?start_date=${start_date}&end_date=${end_date}&${api_key}`;
    return api_url;
  };
}

