import React, { Component } from "react";

class StockMarket extends Component {
  purchase(e) {
    var value =
      e.currentTarget.parentNode.previousSibling.firstElementChild.value;
    if (value === null || value === "") {
      alert("Purchase Amount Needed");
      e.currentTarget.parentNode.previousSibling.firstElementChild.value = "";
      return;
    }
    this.props.purchase(this.props.stock.symbol, value);
    e.currentTarget.parentNode.previousSibling.firstElementChild.value = "";
  }

  render() {
    return (
      <tr>
        <td id="symbol" style={{ width: "15%" }}>
          {this.props.stock.symbol}
        </td>
        <td style={{ width: "45%" }}>{this.props.stock.company}</td>
        <td style={{ width: "20%" }}>${this.props.stock.price}</td>
        <td style={{ width: "10%" }}>
          {" "}
          <input
            type="number"
            className="inputField"
            min="1"
            style={{ width: "100%" }}
          ></input>
        </td>
        <td id="submit" style={{ width: "10%" }}>
          <button type="button" onClick={e => this.purchase(e)}>
            Purchase
          </button>
        </td>
      </tr>
    );
  }
}

export default StockMarket;
